import React, { useState, useRef, useEffect } from 'react';
import { Send, Sparkles, Bot, Zap, Search, Brain, Trash2, Download } from 'lucide-react';

const AIProviders = {
  CHATGPT: { name: 'ChatGPT', icon: Brain, color: 'from-green-400 to-cyan-500' },
  CLAUDE: { name: 'Claude', icon: Sparkles, color: 'from-orange-400 to-pink-500' },
  GROK: { name: 'Grok', icon: Zap, color: 'from-purple-400 to-blue-500' },
  PERPLEXITY: { name: 'Perplexity', icon: Search, color: 'from-blue-400 to-indigo-500' }
};

export default function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [selectedAI, setSelectedAI] = useState('CHATGPT');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      text: input,
      sender: 'user',
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:8000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: input,
          provider: selectedAI.toLowerCase(),
          conversation_history: messages
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      const aiMessage = {
        id: Date.now() + 1,
        text: data.response || 'Sorry, I encountered an error.',
        sender: 'ai',
        provider: selectedAI,
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        id: Date.now() + 1,
        text: 'Failed to connect to backend. Make sure your FastAPI server is running on http://localhost:8000',
        sender: 'ai',
        provider: selectedAI,
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const clearChat = () => {
    if (window.confirm('Are you sure you want to clear the chat?')) {
      setMessages([]);
    }
  };

  const exportChat = () => {
    const chatText = messages.map(msg => 
      `[${msg.timestamp}] ${msg.sender === 'user' ? 'You' : AIProviders[msg.provider].name}: ${msg.text}`
    ).join('\n\n');
    
    const blob = new Blob([chatText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `chat-${new Date().toISOString()}.txt`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="flex flex-col h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
      {/* Header */}
      <div className="bg-black/30 backdrop-blur-lg border-b border-white/10 p-4">
        <div className="max-w-4xl mx-auto">
          <div className="flex items-center justify-between mb-4">
            <h1 className="text-2xl font-bold text-white flex items-center gap-2">
              <Bot className="w-8 h-8" />
              Multi-AI Chatbot
            </h1>
            <div className="flex gap-2">
              {messages.length > 0 && (
                <>
                  <button
                    onClick={exportChat}
                    className="p-2 rounded-lg bg-white/10 text-white hover:bg-white/20 transition-all"
                    title="Export chat"
                  >
                    <Download className="w-5 h-5" />
                  </button>
                  <button
                    onClick={clearChat}
                    className="p-2 rounded-lg bg-white/10 text-white hover:bg-red-500/20 transition-all"
                    title="Clear chat"
                  >
                    <Trash2 className="w-5 h-5" />
                  </button>
                </>
              )}
            </div>
          </div>
          
          {/* AI Provider Selector */}
          <div className="flex gap-2 flex-wrap">
            {Object.entries(AIProviders).map(([key, provider]) => {
              const Icon = provider.icon;
              return (
                <button
                  key={key}
                  onClick={() => setSelectedAI(key)}
                  disabled={isLoading}
                  className={`flex items-center gap-2 px-4 py-2 rounded-lg transition-all ${
                    selectedAI === key
                      ? `bg-gradient-to-r ${provider.color} text-white shadow-lg scale-105`
                      : 'bg-white/10 text-white/70 hover:bg-white/20'
                  } ${isLoading ? 'opacity-50 cursor-not-allowed' : ''}`}
                >
                  <Icon className="w-4 h-4" />
                  {provider.name}
                </button>
              );
            })}
          </div>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4">
        <div className="max-w-4xl mx-auto space-y-4">
          {messages.length === 0 && (
            <div className="text-center text-white/50 mt-20">
              <Bot className="w-16 h-16 mx-auto mb-4 opacity-50" />
              <p className="text-xl mb-2">Select an AI provider and start chatting!</p>
              <p className="text-sm">Choose from ChatGPT, Claude, Grok, or Perplexity</p>
            </div>
          )}
          
          {messages.map((message) => (
            <div
              key={message.id}
              className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`max-w-2xl rounded-2xl px-4 py-3 ${
                  message.sender === 'user'
                    ? 'bg-gradient-to-r from-blue-500 to-purple-500 text-white'
                    : 'bg-white/10 backdrop-blur-lg text-white border border-white/20'
                }`}
              >
                {message.sender === 'ai' && (
                  <div className="flex items-center gap-2 mb-1 text-xs opacity-70">
                    {React.createElement(AIProviders[message.provider].icon, { className: 'w-3 h-3' })}
                    {AIProviders[message.provider].name}
                  </div>
                )}
                <p className="whitespace-pre-wrap break-words">{message.text}</p>
                <div className="text-xs opacity-50 mt-1">{message.timestamp}</div>
              </div>
            </div>
          ))}
          
          {isLoading && (
            <div className="flex justify-start">
              <div className="bg-white/10 backdrop-blur-lg text-white border border-white/20 rounded-2xl px-4 py-3">
                <div className="flex gap-1">
                  <div className="w-2 h-2 bg-white/60 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                  <div className="w-2 h-2 bg-white/60 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
                  <div className="w-2 h-2 bg-white/60 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                </div>
              </div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Input */}
      <div className="bg-black/30 backdrop-blur-lg border-t border-white/10 p-4">
        <div className="max-w-4xl mx-auto flex gap-2">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder={`Ask ${AIProviders[selectedAI].name} anything...`}
            className="flex-1 bg-white/10 text-white placeholder-white/40 rounded-xl px-4 py-3 border border-white/20 focus:outline-none focus:ring-2 focus:ring-purple-500 resize-none"
            rows="2"
            disabled={isLoading}
          />
          <button
            onClick={sendMessage}
            disabled={isLoading || !input.trim()}
            className={`px-6 rounded-xl bg-gradient-to-r ${AIProviders[selectedAI].color} text-white font-semibold transition-all ${
              isLoading || !input.trim() ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105 shadow-lg'
            }`}
          >
            <Send className="w-5 h-5" />
          </button>
        </div>
        <div className="max-w-4xl mx-auto mt-2 text-center text-white/30 text-xs">
          Press Enter to send â€¢ Shift+Enter for new line
        </div>
      </div>
    </div>
  );
}
